import React from 'react';

export default class AddToList extends React.Component {

    state = { newItem: '' };

    render() {
        return (
            <div className="d-flex justify-content-center">            
                <form className="form-group" onSubmit={() => {this.props.onAdd(this.state.newItem)}}>
                <input
                    type='text'
                    className='input'
                    placeholder='Enter Item'
                    onChange={(text) => {this.setState({newItem: text.target.value})}}
                />
                <button type="button" className="btn btn-primary btn-sm">Submit</button>
                </form>
            </div>
        )
    }
}
